import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { jwtDecode } from 'jwt-decode'; // Import jwtDecode as a named export
import './styles/SignIn.css'; // Import the CSS file for styling
import { signInWithEmail, signInWithGoogle } from '../services/authService'; // Import the mock backend service

const SignIn = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [showPassword, setShowPassword] = useState(false);
  const [errorMessage, setErrorMessage] = useState('');
  const navigate = useNavigate();

  const handleGoogleSignIn = async (response) => {
    const userObject = jwtDecode(response.credential);
    const googleEmail = userObject.email;
    const googleResponse = await signInWithGoogle(googleEmail);
    if (googleResponse.success) {
      navigate('/dashboard');
    } else {
      setErrorMessage('Invalid Google account. Please try again or create an account.');
    }
  };

  useEffect(() => {
    const initializeGoogleSignIn = () => {
      /* global google */
      google.accounts.id.initialize({
        client_id: '455880184038-072n72giealrqsbo879tos8964q2bf8q.apps.googleusercontent.com', // Replace with your actual Google client ID
        callback: handleGoogleSignIn,
      });
      google.accounts.id.renderButton(
        document.getElementById('google-sign-in-button'),
        { theme: 'outline', size: 'large' }
      );
    };

    if (window.google) {
      initializeGoogleSignIn();
    } else {
      window.addEventListener('load', initializeGoogleSignIn);
    }

    return () => {
      window.removeEventListener('load', initializeGoogleSignIn);
    };
  }, [handleGoogleSignIn]); // Add handleGoogleSignIn to the dependency array

  const handleEmailSignIn = async () => {
    const response = await signInWithEmail(email, password);
    if (response.success) {
      navigate('/dashboard');
    } else {
      setErrorMessage('Invalid email or password. Please try again or create an account.');
    }
  };

  return (
    <div className="sign-in">
      <h2>Sign In</h2>
      {errorMessage && <p className="error-message">{errorMessage}</p>}
      <div className="form-group">
        <label>Email</label>
        <input
          type="email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          placeholder="Enter your email"
        />
      </div>
      <div className="form-group">
        <label>Password</label>
        <div className="password-verification">
          <input
            type={showPassword ? "text" : "password"}
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            placeholder="Enter your password"
          />
          <button
            type="button"
            onClick={() => setShowPassword(!showPassword)}
          >
            {showPassword ? "Hide" : "Show"}
          </button>
        </div>
      </div>
      <div className="sign-in-btn">
        <button onClick={handleEmailSignIn}>Sign In</button>
      </div>
      <div className="or-text">OR</div>
      <div className="google-sign-in">
        <div id="google-sign-in-button"></div>
      </div>
      <div className="create-account">
        <p>Don't have an account? <a href="/signup">Create an account</a></p>
      </div>
    </div>
  );
};

export default SignIn;
