import React from 'react';
import { Link, Route, Routes, useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import MyProfile from './MyProfile'; // Import MyProfile component
import './styles/Dashboard.css'; // Import the CSS file for styling

const Dashboard = () => {
  const { logout, userProfile } = useAuth();
  const navigate = useNavigate();

  const handleLogout = () => {
    logout();
    navigate('/');
  };

  return (
    <div className="dashboard">
      <div className="sidebar">
        <ul className="sidebar-menu">
          <li>New Funded Account</li>
          <li>Funded Accounts</li>
          <li>Free Trial</li>
          <li>Cashback</li>
          <li>Community Chat</li>
          <li>Affiliate</li>
          <li>Promotions</li>
          <li>Payout</li>
          <li>Market Highlights</li>
          <li>Trading Academy</li>
          <li>Billing</li>
          <li>
            <Link to="/dashboard/profile" className="btn-link">
              {userProfile.profilePic ? (
                <img src={URL.createObjectURL(userProfile.profilePic)} alt="Profile" className="profile-pic" />
              ) : (
                <img src="/path/to/default/profile-pic.png" alt="Default Profile" className="profile-pic" />
              )}
              {userProfile.firstName || 'My Profile'}
            </Link>
          </li>
          <li onClick={handleLogout}>Logout</li>
        </ul>
      </div>
      <div className="content">
        <Routes>
          <Route path="/" element={<div>Welcome to your FutureXtrader dashboard!</div>} />
          <Route path="profile" element={<MyProfile />} />
        </Routes>
      </div>
    </div>
  );
};

export default Dashboard;
